mapper.select:
SELECT indexId, writer, title, body, inserted
FROM ServiceCenter




			WHERE title LIKE #{keyword } OR
			      body LIKE #{keyword }


			WHERE title LIKE #{keyword }


			WHERE body LIKE #{keyword }




ORDER BY indexId DESC
LIMIT #{from}, #{row}

SELECT indexId, writer, title, body, inserted
FROM ServiceCenter
WHERE indexId = #{indexId}

SELECT Count(indexId)
FROM ServiceCenter




			WHERE title LIKE #{keyword } OR
			body LIKE #{keyword }


			WHERE title LIKE #{keyword }


			WHERE body LIKE #{keyword }

mapper.update:
UPDATE ServiceCenter
SET title = #{title},
    body = #{body}
WHERE indexId = #{indexId}

mapper.select:
SELECT fileName
FROM ServiceCenterNoticeFile
WHERE noticeIndexId = #{noticeIndexId}

mapper.delete:
DELETE FROM ServiceCenterNoticeFile
WHERE noticeIndexId = #{indexId}
   AND fileName = #{fileName}

mapper.select:
SELECT indexId, body
FROM ServiceCenter




			WHERE title LIKE #{keyword } OR
			      body LIKE #{keyword }



			WHERE body LIKE #{keyword }




ORDER BY indexId DESC
LIMIT #{from}, #{row}
